def default_api_key(api_key: str | None = None) -> str | None:
    if api_key:
        return api_key

    if os.environ.get("DOCX_API_KEY"):
        return os.environ.get("DOCX_API_KEY")

    raise error.AuthenticationError(
        docx.constants.MISSING_API_KEY_ERROR_MESSAGE
        )
